import { Component } from '@angular/core';
import { FacturasService } from 'src/app/components/facturas.service';
import { Factura } from 'src/models/Facturas';
import { observa }

@Component({
  selector: 'app-facturas',
  templateUrl: './facturas.component.html',
  styleUrls: ['./facturas.component.css']
})
export class FacturasComponent {

  facturas: Factura[] = [];
  clienteId: number = 1;

  constructor(private facturaService: FacturasService) { }


  ngOnInit(): void {
    this.buscarFacturasPorCliente();
  }

  buscarFacturasPorCliente() {
    this.facturaService.buscarPorCliente(this.clienteId)
      .subscribe(facturas => {
        this.facturas = facturas;
      });
  }

}
